mapper.select:
select
from SYS_USER su left join sys_staff_info ssi on su.USER_ID_ = ssi.USER_ID_ left join sys_account sa on su.USER_ID_ = sa.USER_ID_ left join sys_user_position sup on su.USER_ID_ = sup.USER_ID_ and sup.IS_MAJOR_ = 1 left join sys_position sp on sup.POSITION_ID_ = sp.POSITION_ID_ left join sys_organization so on sp.ORG_ID_ = so.ORG_ID_ left join sys_job sj on sp.JOB_ID_ = sj.JOB_ID_
su.USER_ID_ = #{idValue}

select count(
    su.USER_ID_
) as counts
from SYS_USER su


        AND su.USER_ID_ != #{userId}


        AND su.TENANT_ID_ = #{tenantId}


        AND su.USER_NAME_ = #{userName}


        AND su.ENG_NAME_ = #{engName}


        AND su.GENDER_ = #{gender}


        AND su.BIRTHDAY_ = #{birthday}


        AND su.MOBILE_ = #{mobile}


        AND su.EMAIL_ = #{email}


        AND su.ADDRESS_ = #{address}


        AND su.TELEPHONE_ = #{telephone}


        AND su.DESCR_ = #{descr}


        AND su.IS_DELETE_ = #{isDelete}


        AND su.IS_FINAL_ = #{isFinal}


        AND su.IS_VISIBLE_ = #{isVisible}


        AND su.CRT_TIME_ = #{crtTime}


        AND su.UPD_TIME_ = #{updTime}


        AND su.CRT_USER_ID_ = #{crtUserId}


        AND su.UPD_USER_ID_ = #{updUserId}


        AND su.HEAD_PHOTO_ = #{headPhoto}

mapper.sql:
AND su.USER_ID_ != 1 AND ssi.STAFF_STATUS_ = 1
AND su.IS_DELETE_=#{isDelete}
AND su.USER_NAME_ like '%${userName}%'
AND su.ENG_NAME_ like '%${engName}%'
AND su.GENDER_ = #{gender}
${filterStr}
AND EXISTS(SELECT sp.POSITION_ID_ FROM sys_position sp INNER JOIN sys_user_position sup ON sp.POSITION_ID_ = sup.POSITION_ID_ WHERE sp.POSITION_ID_ = #{userPosition.positionId} AND sup.USER_ID_ = su.USER_ID_ )
AND EXISTS( SELECT ORG_ID_ FROM ( SELECT ORG_ID_ FROM SYS_ORGANIZATION WHERE IS_DELETE_ = 0 START WITH ORG_ID_ = #{userPosition.orgId} CONNECT BY PRIOR ORG_ID_ = FID_) WHERE ORG_ID_ = sp.ORG_ID_)
AND EXISTS(SELECT sp.POSITION_ID_ FROM sys_position sp INNER JOIN sys_user_position sup ON sp.POSITION_ID_ = sup.POSITION_ID_ WHERE FIND_IN_SET(sp.ORG_ID_, get_org_children(#{userPosition.orgId})) AND sup.USER_ID_ = su.USER_ID_ )

mapper.select:
select from SYS_USER su left join sys_user_position sup on su.USER_ID_ = sup.USER_ID_ and sup.IS_MAJOR_ = 1 left join sys_position sp on sup.POSITION_ID_ = sp.POSITION_ID_ left join sys_staff_info ssi on su.USER_ID_ = ssi.USER_ID_
ORDER BY ${orderStr}
ORDER BY su.USER_ID_ desc

select
from SYS_USER su left join sys_staff_info ssi on su.USER_ID_ = ssi.USER_ID_ left join sys_account sa on su.USER_ID_ = sa.USER_ID_ left join sys_user_position sup on su.USER_ID_ = sup.USER_ID_ and sup.IS_MAJOR_ = 1 left join sys_position sp on sup.POSITION_ID_ = sp.POSITION_ID_ left join sys_organization so on sp.ORG_ID_ = so.ORG_ID_ left join sys_job sj on sp.JOB_ID_ = sj.JOB_ID_
sa.LOGIN_NAME_ = #{idValue}
